#pragma once

namespace WAVM {
	static bool isNameChar(char c)
	{
		return (c >= 'a' && c <= 'z') || (c >= 'A' && c <= 'Z') || (c >= '0' && c <= '9')
			   || c == '_' || c == '\'' || c == '+' || c == '-' || c == '*' || c == '/' || c == '\\'
			   || c == '^' || c == '~' || c == '=' || c == '<' || c == '>' || c == '!' || c == '?'
			   || c == '@' || c == '#' || c == '$' || c == '%' || c == '&' || c == '|' || c == ':'
			   || c == '`' || c == '.';
	}
}
